import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './App.css';
import { Button, ButtonGroup } from '@mui/material';

export default function App() {

  const styles = {
    "&.MuiButton-root": {
      border: "1px #8884d8 solid"
    },
    "&.MuiButton-text": {
      color: "#8884d8"
    },
    "&.MuiButton-contained": {
      color: "#8884d8"
    },
    "&.MuiButton-outlined": {
    color: "#8884d8"
    }
  };

  const timeIntervals = {
    Day: {days: 1, slice: 5, length: 10},
    Week: {days: 7, slice: 5, length: 10},
    Month: {days: 30, slice: 5, length: 7},
  };

  const [graphData, setGraphData] = useState([]);
  const [activeButton, setActiveButton] = useState("Day");

  useEffect (() => {
    fetchData("Day");
  },[]);

  const fetchData = (interval) => {
    const { days, slice, length } = timeIntervals[interval];
    fetch(`https://dev-backend.rydecarpool.com/coding-challenge/signups?target=dev`)
      .then(response => response.json()) 
      .then(data => {
        const transformedData = [];
        let sum = 0;
        for (let i = 0; i < data.length; i++) {
          sum += data[i].count;
          if ((i + 1) % days === 0) {
            transformedData.push({
              count: sum,
              date: data[i].date.slice(slice, length)
            });
            sum = 0;
          }
        }
        if (sum > 0) {
          transformedData.push({
            count: sum,
            date: data[data.length - 1].date.slice(slice, length)
          });
        }
        setGraphData(transformedData);
      })
      .catch(err => console.log(err))
  };

  const handleButtonClick = (label) => {
    setActiveButton(label);
    fetchData(label);
  };

    return (
      <main>
        <h2>Ryde User Visualization: October-December 2022</h2>
        <div className="graph">
          <ResponsiveContainer width="100%" aspect = {3}>
            <LineChart
              width={500}
              height={300}
              data={graphData}
              margin={{
                top: 5,
                right: 30,
                left: 20,
                bottom: 5,
              }}
            >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date"/>
          <YAxis dataKey="count"/>
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="count" stroke="#8884d8" activeDot={{ r: 8 }} />
          </LineChart>
          </ResponsiveContainer>
        </div>
        <div className = "buttonGroup">
            <ButtonGroup variant="outlined" aria-label="outlined primary button group">
                <Button onClick={() => handleButtonClick("Day")} sx={styles}>Day</Button>
                <Button onClick={() => handleButtonClick("Week")} sx={styles}>Week</Button>
                <Button onClick={() => handleButtonClick("Month")} sx={styles}>Month</Button>
            </ButtonGroup>
          </div>
      </main>
    );
}
