import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './App.css';
import { Button, ButtonGroup } from '@mui/material';



export default function App() {

  const styles = {
    "&.MuiButton-root": {
      border: "1px #8884d8 solid"
    },
    "&.MuiButton-text": {
      color: "#8884d8"
    },
    "&.MuiButton-contained": {
      color: "#8884d8"
    },
    "&.MuiButton-outlined": {
    color: "#8884d8"
    }
  };

  const [graphData, setGraphData] = useState([]);
  const [activeButton, setActiveButton] = useState("Day");

  useEffect (() => {
    fetch(`https://dev-backend.rydecarpool.com/coding-challenge/signups?target=dev`)
    .then(response => response.json()) 
    .then(data => {
         console.log(data)
         const transformedData = data.map(item => ({ count: item.count, date: item.date.slice(5, 10) }));
         setGraphData(transformedData);
      })
    .catch(err => console.log(err))
  },[]);

  const handleButtonClick = (label) => {
    setActiveButton(label);
    // Update graph data based on button click
    // For example, if label is "Day", fetch and set data for the past day

    if (label == "Day"){ 
      console.log("Day Pressed")
    }
    else if (label == "Day"){ 
      console.log("Day Pressed")
    }
  };

    return (
      <main>

        <div className="graph">
          <div className = "buttonGroup">
            <ButtonGroup variant="outlined" aria-label="outlined primary button group">
                <Button onClick={() => handleButtonClick("Day")} sx={styles}>Day</Button>
                <Button sx={styles}>Week</Button>
                <Button sx={styles}>Month</Button>
            </ButtonGroup>
          </div>
          <ResponsiveContainer width="100%" aspect = {3}>
            <LineChart
              width={500}
              height={300}
              data={graphData}
              margin={{
                top: 5,
                right: 30,
                left: 20,
                bottom: 5,
              }}
            >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" domain={['1', '2']}/>
          <YAxis dataKey="count"/>
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="count" stroke="#8884d8" activeDot={{ r: 8 }} />
          </LineChart>
          </ResponsiveContainer>
        </div>
      </main>
    );
}
