import React, { PureComponent } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './App.css'

// fetch('https://dev-backend.rydecarpool.com/coding-challenge/signups?target=dev')
//   .then(response => response.json())
//   .then(data => {
//     // Process the retrieved data here
//     console.log(data);
//   })
//   .catch(error => {
//     console.log(error);
//   });

useEffect (() => {
  window.scrollTo(0, 0)
  fetch(`http://localhost:3001/recipes/${name}`)
  .then(response => response.json()) 
  .then(recipeData => {
      setRecipe(recipeData[0])
      setAllIngredients(recipeData[0].ingredients)
      setAllSteps(recipeData[0].instructions)
      console.log(recipeData[0])})
  .catch(err => console.log(err))
},[name]);


const data = [
  {
    name: 'Page A',
    pv: 2400,
  },
  {
    name: 'Page B',
    pv: 1398,
  },
  {
    name: 'Page C',
    pv: 9800,
  },
  {
    name: 'Page D',
    pv: 3908,
  },
  {
    name: 'Page E',
    pv: 4800,
  },
  {
    name: 'Page F',
    pv: 3800,
  },
  {
    name: 'Page G',
    pv: 4300,
  },
];

export default function App() {

    return (
      <div class="graph">
      <ResponsiveContainer width="100%" aspect = {3}>
        <LineChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />
          <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
        </LineChart>
      </ResponsiveContainer>
      </div>
    );
}
